# This function block helps to create a nice HTML format to visualize

def generate_html_table(df):

    css_style = """table.paleBlueRows {
      font-family: "Trebuchet MS", Helvetica, sans-serif;
      border: 1px solid #FFFFFF;
      width: 100%;
      height: 150px;
      text-align: center;
      border-collapse: collapse;
    }
    table.paleBlueRows td, table.paleBlueRows th {
      text-align: center;
      border: 1px solid #FFFFFF;
      padding: 3px 2px;
    }
    table.paleBlueRows tbody td {
      text-align: center;
      font-size: 11px;
    }
    table.paleBlueRows tr:nth-child(even) {
      background: #D0E4F5;
    }
    table.paleBlueRows thead {
      background: #0B6FA4;
      border-bottom: 5px solid #FFFFFF;
    }
    table.paleBlueRows thead th {
      font-size: 17px;
      font-weight: bold;
      color: #FFFFFF;
      border-left: 2px solid #FFFFFF;
    }
    table.paleBlueRows thead th:first-child {
      border-left: none;
    }

    table.paleBlueRows tfoot {
      font-size: 14px;
      font-weight: bold;
      color: #333333;
      background: #D0E4F5;
      border-top: 3px solid #444444;
    }
    table.paleBlueRows tfoot td {
      font-size: 14px;
    }
    div.scrollable {width:100%; max-height:150px; overflow:auto; text-align: center;}
    """

    from yattag import Doc, indent
    doc, tag, text, line = Doc().ttl()

    with tag("head"):
        with tag("style"):
            text(css_style)

    with tag('table', klass='paleBlueRows'):
        with tag("tr"):
            for col in list(df.columns):
                with tag("th"):
                     with tag("div", klass = "scrollable"):
                        text(col)
        for idx, row in df.iterrows():
            with tag('tr'):
                for i in range(len(row)):
                    with tag('td'):
                        with tag("div", klass = "scrollable"):
                            try:
                                if "http" in str(row[i]):
                                    with tag("a", href = str(row[i])):
                                        text(str(row[i]))
                                else:
                                    text(str(row[i]))
                            except:
                                print(row[i])

    #display(HTML(doc.getvalue()))
    return(doc.getvalue())

