def process(text):
    with open (text, "r", encoding = "ISO-8859-1") as file:
        data=file.readlines()
        script = ""
        for i in data:
            i = i.lower().replace('"', '').replace("\n", " \n ")
            if i.strip() != "":
                script += "".join(i).replace("\n"," \n ")
        return script
