            item[1] *= p[0]
    elif(item[2]=='fr'):
        if(item[1]<0.7):
            item[1] *= p[1]
    elif(item[2]=='ru'):
        if(item[1]<0.7):
            item[1] *= p[2]
    elif(item[2]=='it'):
        if(item[1]<0.7):
            item[1] *= p[3]
    elif(item[2]=='tr'):
        if(item[1]<0.7):
            item[1] *= p[4]
    elif(item[2]=='pt'):
        if(item[1]<0.7):
            item[1] *= p[5]

    out.append([item[0], item[1]])

of = pd.DataFrame(out, columns=['id', 'toxic'])
