score1 = roc_auc_score(mybest.toxic.round().astype(int), of.toxic.values)
score2 = roc_auc_score(of.toxic.round().astype(int), mybest.toxic.values)
print('%2.4f\t%2.4f'%(100*score1, 100*score2))

