def get_batches():
    return pd.read_csv(
        '/kaggle/input/airbnb-recruiting-new-user-bookings/sessions.csv.zip',
        usecols=['user_id', 'action_type', 'action_detail', 'secs_elapsed'],
        chunksize=10000
    )

