        for p in ax.patches:
            ax.text(p.get_x()+p.get_width()/2, p.get_height()*1.02,
                    df[var[i]].value_counts().iloc[j], ha='center')
            j += 1
        ax.set_title(label=titles[i], fontsize=16, fontweight='bold', pad=10)
        ax.set_ylabel(None)
