# Fill ´none´ titles with the same value as ´original_title´
def imput_title(df):
    df['title'] = df['title'].fillna("none")

    for index, row in df.iterrows():
        if row['title'] == "none":
            df.at[index,'title'] = df.loc[index]['original_title']
    return df

