def index_classify(ind):
    index_cat = ['Medium','High']
    if 55.0 <= ind <= 69.9:
        return index_cat[0]
    elif 70.0 <= ind <= 79.9:
        return index_cat[1]
    else:
        return 'Out of Bounds'

#Applying the function
data_mini['index_category'] = data_mini['Index'].apply(index_classify)
