def get_similar(data):
  similar = []
  for i in range(0, data.shape[0]):
    similar.append(str(data['Name'][i]) + ' '+ str(data['Writer'][i]) + ' '+ str(data['Genre'][i]) + ' '+ str(data['Update'][i]))

  return similar
