def police(speed, is_birthday):
    birthday = 0
    if is_birthday:
        birthday = 5
    if speed <= 60 + birthday:
        print('No Ticket')
    elif 60 + birthday < speed <= 80 + birthday:
        print('Small Ticket')
    elif speed > 80 + birthday:
        print('Big Ticket')


police(62, True)
