import pandas as pd
Reviews=pd.DataFrame()
for b in range(len(links)):
    link_2=links[b]
    source_2=requests.get(link_2).text
    soup=BeautifulSoup(source_2,"lxml")
    review=soup.find_all('div',class_="wt-content-toggle--truncated-inline-multi")
    reviews_web=[]
    for i in range(len(review)):
        for text in review[i].findAll('p'):
            reviews_web.append(text.get_text().strip())
    for c in range(len(reviews_web)):
        Reviews=Reviews.append({'Product Review': reviews_web[c]}, ignore_index=True)
