def duzenle(df):
    kolon = []
    for i in df.columns:
        if "(" in str(i) :
            index = i.find("(")
            i = i.replace(i[index-1:] , "")
        elif "." in str(i):
            index = i.find(".")
            i = i.replace(i[index:] , "")
        i = i.replace(" " , "_")
        kolon.append(i)
    df.columns = kolon

