class PixelImageItemList(ImageList):

    def open(self,fn):
        img_pixel = self.inner_df.loc[self.inner_df['fn'] == int(fn[2:])].values[0,1:785]
        img_pixel = img_pixel.reshape(28,28)
        return vision.Image(pil2tensor(img_pixel,np.float32).div_(255))

