#Define the months
def month(x):
    if x==1 :
        return "JANUARY"            
    if x==2:
        return "FEBRUARY"            
    if x==3:
        return "MARCH"           
    if x==4:
        return "APRIL"            
    if x==5:
        return "MAY"
    if x==6:
        return "JUNE"

covid_df1['month'] = covid_df1['month'].map(lambda x : month(x))

