def findflower(petal_length):
    f = 0
    if (petal_length < 1.9):
        f =  'Setosa'
    elif (petal_length > 3.2 and petal_length < 5):
        f =  'Versicolor'
    elif (petal_length > 5):
        f =  'Virginica'
    return f
