# Function to get the data
def count_platform(data):
    dic={'Netflix':0,'Hulu':0,'Disney+':0,'Prime Video':0}
    for i in data[['Netflix','Hulu','Disney+','Prime Video']].values:
        if i[0]==1:
            dic['Netflix']+=1
        if i[1]==1:
            dic['Hulu']+=1
        if i[2]==1:
            dic['Disney+']+=1
        if i[3]==1:
            dic['Prime Video']+=1
    return dic
dic=count_platform(data)
fig = px.pie(data, values=dic.values(), names=dic.keys(), title='Platform Distribution of this data')
fig.show()

