def week_in_month(dt):
    weekday_one = datetime.date(dt.year, dt.month, 1).weekday()
    weekday_day = datetime.date(dt.year, dt.month, dt.day).weekday()
    x=(dt.day - 1)//7 + 1 + (weekday_day < weekday_one)
    #print(x,' ',dt)
    return 'week '+str(x)

