def plot_maker(cat_feature):
    plot = alt.Chart(df).mark_bar().encode(
        y = cat_feature,
        x = "count()"
    ).interactive()
    return plot

source.e7e7919e:
def histogram_maker(num_category,max_):
    plot = alt.Chart(df).mark_bar().encode(
        alt.Y(num_category, bin= {"binned": False, "step": 0.05 , "extent" : [0,max_]}),
        x='count()'
    )
    return(plot)

