source.88573561:
def tag_count(tag):
    count = []
    unique = df[tag].unique()
    for x in unique:
        count.append(df[df[tag] == x][tag].count())
    posts_df = pd.DataFrame({
    "Posts" : unique,
    "Count" : count
})
    print(tag, "Top 10")
    display(posts_df.sort_values(by = "Count" , ascending = False).head(10))

