def return_sorted(col_name, asc = False, limit = 5):
    sorted_df = df.sort_values(by=col_name, ascending=asc)
    sorted_df = sorted_df[:limit]

    return {'weapon': sorted_df['Weapon Name'].to_list(), 'values': sorted_df[col_name].to_list()}
