def read_corpus(docs, tokens_only=False):
    for index, rec in enumerate(docs):
        doc = rec[0]
        tag = rec[1]
        tag = ''.join([tag, '_', str(index)])
        tokens = gensim.utils.simple_preprocess(doc)
        if tokens_only:
            yield tokens
        else:
            yield gensim.models.doc2vec.TaggedDocument(tokens, [tag])
