###Creating a disctionary with lists
music = {'name': ['Frankenstein', 'Waste', 'Make Light'],
         'band':['TPC', 'FTP', 'PP'],
         'album': ['Champ', 'Torches', 'Manners']
         }
###Looping though the dictionary with lists

#By key
for key, value in music.items():
        print(key)


#By value       
for key, value in music.items():
    for element in value:
        print(value)

#By element in list
for key, value in music.items():
    for element in value:
        print(element)

