# print percent of value
def price_percentage_to_string(start, stop, step):
    p = np.arange(start, stop, step)
    prev = 'start'
    try:
        for i in p:
            if prev != content[i]:
                msg = '가격이 ' + str(content[i]) + '$ 이내에 분포하는 숙소는 전체 데이터의 ' + str(i) + '퍼센트입니다.'
                print(msg)
                prev = content[i]
    except IndexError:
        pass
price_percentage_to_string(0, 100, 1)

