def my_split(text,split_by_chr):
    ouput_list = list()

    for chr in text: 
        if chr == split_by_chr:
            ouput_list.append('')
            continue

        if len(ouput_list) == 0:
            ouput_list.append('')

        ouput_list[-1] += (chr)

    if ouput_list[-1] == '':
        ouput_list.pop()

    return ouput_list

