def get_status(my_speed,is_my_birthday):

    birthday_speed = 5 if is_my_birthday else 0

    my_speed -= birthday_speed

    if my_speed < 60 :
        return 'No Ticket'

    elif 80 > my_speed >= 60 :
        return 'Small Ticket'

    elif my_speed >= 80 :
        return 'Big Ticket'

status = get_status(my_speed,is_my_birthday)
