conditions_with_birthday = {}
conditions_with_no_birthday = {}

def pre_process():
    for i in range(60):
        conditions_with_no_birthday[i] = 'No Ticket'

    for i in range(60,80):
        conditions_with_no_birthday[i] = 'Small Ticket'

    for i in range(80,320):
        conditions_with_no_birthday[i] = 'Big Ticket'

    for i in range(60+5):
        conditions_with_birthday[i] = 'No Ticket'

    for i in range(60,80+5):
        conditions_with_birthday[i] = 'Small Ticket'

    for i in range(80+5,320):
        conditions_with_birthday[i] = 'Big Ticket'


def get_status(my_speed,is_my_birthday):
    if is_my_birthday:
        return conditions_with_birthday[my_speed]
    else:
        return conditions_with_no_birthday[my_speed]


pre_process()
status = get_status(my_speed,is_my_birthday)

print(status)
