def plotgraph(testy,pred,regressorName):
    if max(testy)>=max(pred):
        myrange=int(max(testy))
    else:
        myrange=int(max(pred))
    plt.scatter(testy,pred,color="red")
    plt.plot(range(myrange),range(myrange),'o')
    plt.title(regressorName)
    plt.show()
    return

