def plot_pie(col,name):
    levels = train[col].unique()
    values = []
    labels = []
    for level in levels:
        labels.append(level)
        temp = train.loc[train[col] == level]
        val = temp.Target.value_counts().sum()
        values.append(val)


    fig = {
      "data": [
        {
          "values": values,
          "labels": labels,
#           "domain": {"x": [0,0]},
          "name": name,
          "hoverinfo":"label+percent+name",
          "hole": .4,
          "type": "pie"
        }],
      "layout": {
            "title":name,
            "annotations": [
                {
                    "font": {
                        "size": 20
                    },
                    "showarrow": False,
                    "text": col,
                    "x": 0.5,
                    "y": 0.5
                }
            ]
        }
    }
    iplot(fig, filename='donut')
