def target_name(x):
    if x == 1:
        return "Extreme"
    if x == 2:
        return "Moderate"
    if x == 3:
        return "Vulnerable"
    if x == 4:
        return "Non Vulnerable"
train["Target_name"]= train.Target.apply(lambda x: target_name(x))

