def Ticket(speed):
    birthday = input('Is today your birthday?(yes/no)')
    if birthday == 'yes':
        gift = 5
    else:
        gift = 0
    if speed <= (60+gift):
        return ('No Ticket')
    if speed >= 60 and speed <=(80+gift) :
        return ('Small Ticket')
    if speed >= (80+gift):
        return ('Big Ticket')
Ticket(64)
