from sklearn.metrics import pairwise_distances

def sim_affinity(X):
    return pairwise_distances(X, metric=car_iou)

def car_iou(car1, car2):
    car1 = car1 > 0.5
    car2 = car2 > 0.5
    return 1 - (car1 & car2).sum() / (car1 | car2).sum()

