class SendWandB(ppe.training.extensions.PrintReport):
#     def __init__(self, entries=None, log_report='LogReport', wandb=None):
    def __init__(self, entries=None, log_report='LogReport', wandb=wandb, fold_id=None):
        super().__init__(entries, log_report, None)
        self.wandb = wandb
        self.entries = entries
        self.fold_id = fold_id

    def __call__(self, manager):
        log_report = self.get_log_report(manager)
        log = log_report.log
        log_len = self._log_len
        while len(log) > log_len:
            # self.wandb.log(log[log_len], step=log_len)
            if str(self.fold_id) != 'None':
                send_log = {}
                for k,v in log[log_len].items():
                    if k in self.entries:
                        send_log[f'{k}/fold_{self.fold_id}'] = v
                    elif k == 'epoch':
                        send_log[k] = v
                self.wandb.log(send_log)
            else:
                self.wandb.log(log[log_len])
            log_len += 1
        self._log_len = log_len

