def map_month_to_seasons(month_val):
    if month_val in [12, 1, 2]:
        season_val = 'Winter'
    elif month_val in [3, 4, 5]:
        season_val = 'Summer'
    elif month_val in [6, 7, 8, 9]:
        season_val = 'Monsoon'
    elif month_val in [10, 11]:
        season_val = 'Post_Monsoon'

    return season_val

