def quarter(x):

    dic = {(1, 2, 3): 'Q1',
           (4, 5, 6): 'Q2',
           (7, 8, 9): 'Q3',
           (10, 11, 12): 'Q4'}

    for d in dic:
        if x in d:
            return dic[d]

df2['Quarter'] = df2['Month'].apply(quarter)
