def unique(dataframe, train=True):
    unique_values = []
    if train:
        for col in dataframe.columns[1:-1]:
            unique_values.append(len(dataframe[col].unique()))
    else:
        for col in dataframe.columns[1:]:
            unique_values.append(len(dataframe[col].unique()))
    return unique_values
