def assign_month(df):
    for i in range(0,len(df.index)):
        if '/01' in df.loc[i,'index']:
            df.loc[i,'month']='Jan-21'
        elif '/02' in df.loc[i,'index']:
            df.loc[i,'month']='Feb-21'
        elif '/03' in df.loc[i,'index']:
            df.loc[i,'month']='Mar-21'
