def timing(f):
    @wraps(f)
    def wrap(*args, **kw):
        ts = time()
        result = f(*args, **kw)
        te = time()
        print('func:%r took: %2.4f sec' % \
          (f.__name__, te-ts))
        return result
    return wrap

def shape(f, outputs=True, inputs=True):
    @wraps(f)
    def wrap(*args, **kw):
        ts = time()
        result = f(*args, **kw)
        te = time()

        if inputs:
            print('func:%r input shape:%r' % \
              (f.__name__, args[0].shape))

        if outputs:
            print('func:%r output shape:%r' % \
              (f.__name__, result.shape))
        return result
    return wrap
