def GetGrade(Mean,OverAllPassStatus):
    if (OverAllPassStatus == 'F'):
        return 'F'
    if (Mean >= 80):
        return 'A'
    if (Mean >= 70):
        return 'B'
    if (Mean >= 60):
        return 'C'
    if (Mean >= 50):
        return 'D'
    if (Mean >= 40):
        return 'E'
    else: 
        return 'F'

df['GradeStatus'] = df.apply(lambda x: GetGrade(x['Mean'],x['OverAllPassStatus']),axis=1)

df.GradeStatus.value_counts().sort_values(ascending=False)
