mse_loss = nn.MSELoss()

def mcrmse(y_actual, y_pred, num_scored=5):
    score = 0
    for i in range(num_scored):
        score += mse_loss(y_actual[:, :, i], y_pred[:, :, i]) / num_scored
    return score
