country_combo_list = []
correlation = []
cols_done = []
for col_one in df.iloc[:,1:].columns:
    for col_two in df.iloc[:,1:].columns:
        if col_two not in cols_done:
            if col_one != col_two:
                if (df[col_one].corr(df[col_two]) > 0.2):
                    country_combo_list.append(col_one + " correlation with " + col_two)
                    correlation.append(df[col_one].corr(df[col_two]))
    cols_done.append(col_one)
