def zsum_test_2(n1,n2,s1,s2,mu1,mu22,conf_level=0.95,alternative="two.sided"):
    from scipy.stats import norm
    import numpy as np
    if alternative not  in ["two.sided","greater","less"]:
        return "Aternative debe ser uno de two.sided, greater o less"
    if conf_level<=0 or conf_level>=1:
        return "El nivel de confianza debe estar en (0,1)"
    if s1<0:
        return "No se puede realizar el procedimiento porque el valor s es menor que cero"
    if s2<0:
        return "No se puede realizar el procedimiento porque el valor s es menor que cero"
    else:
        alpha=1-conf_level
    if alternative=="two.sided":
            alpha/=2
    zt=norm.ppf(1-alpha)
    li = (mu1-mu22) - zt * np.sqrt(s1**2/n1 + s2**2/n2)
    ls = (mu1-mu22) + zt * np.sqrt(s1**2/n1 + s2**2/n2)
    zc=(mu1-mu22)/(np.sqrt(s1/n1 + s2/n2))
    pval=2*norm.sf(np.abs(zc))
    if alternative=="greater":
        ls="Inf"
        pval=norm.sf(zc)
    if alternative=="less":
        li="-Inf"    
        pval=norm.cdf(zc)
    return('Hipótesis alternativa {}. Estadístico de prueba ={:.4f}, p_value={:f}. Intervalo de confianza al {:.2%}: Li={:.3f} , Ls={:.3f}'.format(alternative,zc,pval,conf_level,li,ls))

