# https://github.com/ipython/ipython/issues/10045
def show_gif(fname):
    import base64
    with open(fname, 'rb') as fd:
        b64 = base64.b64encode(fd.read()).decode('ascii')
    return HTML(f'<img src="data:image/gif;base64,{b64}" />')
