# function which preprocess Release_date column in Year variant
def to_year(date):
  string = list(date)

  if len(string) == 4:
    return int(date)
  elif len(string) == 6:
    date = 1999
  else:
    string = "".join(string)
    date = int(string[-4:])

  return date

# example
print(to_year('July 13, 2006'))

