def toPath(string):
    ''' image_id to the path to image '''
    if ".jpg" not in string:
        string = string + ".jpg"
    return string

def toID(string):
    ''' image path to image_id '''
    if string[-4:] ==".jpg":
        string = string[:-4]
    return string

print(toPath("0123"))
print(toID("0123.jpg"))

