from scipy.special import factorial   # 팩토리얼 매소드 임포트

def Poi(lam):
    x_set = np.arange(20)    # 구현 편의상 1 ~ 19 로 지정
    def f(x):
        if x in x_set:
            return np.power(lam, x) / factorial(x) * np.exp(-lam)
        else:
            return 0
    return x_set, f
