import re

def parse_numeric(s):
    """    
    유로화 문자열 -> 수치형 환산
    값 예시 : €0 또는 €43K
    끝에 K가 있으면 곱하기 1,000
    끝에 M이 있으면 곱하기 1,000,000    
    """
    if s[-1]  == 'K':
        return float(re.findall("\d+", s)[0])*1000    
    elif s[-1]  == 'M':
        return float(re.findall("\d+", s)[0])*1000000
    else:
        return float(re.findall("\d+", s)[0])

wage = df1['Wage'].apply(lambda x: parse_numeric(x))
