# Find the Perfect Number Between 1 and 10000 With User Defined Functions 

def perfect_num(num):

    sum = 0

    for x in range(1,num):

        if (num % x == 0):
            sum +=x
            x +=1
        else:
            x+=1
    if (sum == num):
        return True

for num in range(1,10001):
    if (perfect_num(num)):
        print(num)

