some_countries = ["England", "HerpaDerpaland", "Engla"]

def do_fuzzy_search(country):
    try:
        result = pycountry.countries.search_fuzzy(country)
        return result[0].alpha_3
    except:
        return np.nan

for country in some_countries:
    print(do_fuzzy_search(country))

