def my_agent(wheel):
    last_number = 0
    guess = random.randint(0, 10)
    while wheel.num_remaining_spins() > 0:
        if wheel.num_bets_successful()/(wheel.num_bets_made()+1) > 0.7:
            guess = None if random.randint(0, 1)  <0.5 else last_number
        else:
            guess = random.randint(0, 10)
        last_number = wheel.spin(number_to_bet_on=guess)


