class MP_Neuron:
    def __init__(self):
        self.b=None

    def model(self,x):
        return (sum(x)>=self.b)

    def predict(self,X):
        Y=[]
        for x in X:
            y=self.model(x)
            Y.append(y)
        return np.array(Y)

    def fit(self,X,Y):
        accuracy={}
        for b in range(X.shape[1]+1):
            self.b=b
            Y_pred=self.predict(X)
            accuracy[b]=accuracy_score(Y_pred,Y)
        best_b=max(accuracy,key=accuracy.get)
        self.b=best_b
        print("The optimal value of threshold is=",self.b," with an accuracy=",accuracy[self.b])  
        return accuracy
